import React, { useContext, useState } from 'react';
import { Link, useHistory } from 'react-router-dom';
import { AuthContext } from './AuthContext';
import './styles/connexion.css';

const Inscription = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [loginStatus, setLoginStatus] = useState('');
  const { login } = useContext(AuthContext);

  const history = useHistory();

  const handleLogin = async (e) => {
    e.preventDefault();

    // Vérification du pseudo et du mot de passe via l'API
    try {
      const response = await fetch(
        `http://localhost:8080/Backend/API?action=addCompteUtilisateur&pseudo=${username}&mdp=${password}`
      );

      if (response.ok) {
        const data = await response.json();
        const { res } = data;

        if (res === true) {
          // Succès de la connexion
          login(username); // Met à jour l'état de connexion (~Variable Globale)
          setLoginStatus('Inscription réussie');

          history.push('/connexion');
        } else {
          // Échec de la connexion
          setLoginStatus('Échec de l\'inscription');
        }
      } else {
        setLoginStatus('Échec de l\'inscription');
      }
    } catch (error) {
      console.log('Erreur lors de l\'inscription', error);
    }
  };

  return (
    <div className="container">
      <h2>Connexion</h2>
      <form onSubmit={handleLogin}>
        <input
          type="text"
          placeholder="Nom d'utilisateur"
          required
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
        <input
          type="password"
          placeholder="Mot de passe"
          required
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <input type="submit" value="S'inscrire'" />
        <Link to="/connexion">      
            <input type="button" value="Déjà inscrit ? C'est par ici" />
        </Link>
      </form>
      {loginStatus && <p>{loginStatus}</p>}
    </div>
  );
};

export default Inscription;
