import React, { useContext, useState } from 'react';
import { Link, useHistory } from 'react-router-dom';
import './styles/connexion.css';

const Inscription = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [loginStatus, setLoginStatus] = useState('');

  const history = useHistory();

  const currentDate = new Date();
  const formattedDate = currentDate.toISOString();

  const handleLogin = async (e) => {
    e.preventDefault();

    // Vérification du pseudo et du mot de passe via l'API
    try {
      const response = await fetch(
        `http://localhost:8080/Backend/API?action=addCompteUtilisateur&pseudo=${username}&mdp=${password}&date=${formattedDate}`
      );

      if (response.ok) {
        const data = await response.json();
        const { res } = data;

        if (res === true) {
          // Succès de la connexion
          setLoginStatus('Inscription réussie');

          history.push('/connexion');
        } else {
          // Échec de la connexion
          setLoginStatus('Échec de l\'inscription');
        }
      } else {
        setLoginStatus('Échec de l\'inscription');
      }
    } catch (error) {
      console.log('Erreur lors de l\'inscription', error);
    }

    const nom = event.target.elements['nom'].value;
        const genre = event.target.elements['genre'].value;
        const nationalite = event.target.elements['nationalite'].value;
        const naissance = event.target.elements['naissance'].value;

        // Envoyer les données à l'API en utilisant l'URL avec les bons paramètres
        const url = new URL('http://localhost:8080/Backend/API');
        url.searchParams.append('action', 'addAuteur');
        url.searchParams.append('nom', nom);
        url.searchParams.append('genre', genre);
        url.searchParams.append('nationalite', nationalite);
        url.searchParams.append('naissance', naissance);

        fetch(url, {
        method: 'GET'
        })
        .then(response => {
        if (!response.ok) {
            throw new Error('Une erreur s\'est produite lors de la requête.');
        }        
        // Traiter la réponse si nécessaire
        console.log(url);
        console.log('La requête a été envoyée avec succès.');
        // Rediriger vers une autre page
        history.push('/connexion');
        })
        .catch(error => {
        console.log(url);
        console.error(error);
        });
  };

  return (
    <div className="container">
      <h2>Inscription</h2>
      <form onSubmit={handleLogin}>
        <input
          type="text"
          placeholder="Nom d'utilisateur"
          required
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
        <input
          type="password"
          placeholder="Mot de passe"
          required
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <input type="submit" value="S'inscrire" />
        <Link to="/connexion">      
            <input type="button" value="Déjà inscrit ? C'est par ici" />
        </Link>
      </form>
    </div>
  );
};

export default Inscription;
