import React, { useEffect, useState } from 'react';
import './styles/ficheManga.css';

function FicheManga(props) {
  const [manga, setManga] = useState(null);

  useEffect(() => {
    const { id } = props.match.params;
    //const apiUrl = `http://localhost:8080/Backend/API?action=getManga&id=${id}`;
    const apiUrl = `file:///C:/Users/Antoine/Desktop/test.json`;    

    fetch(apiUrl)
      .then(response => response.json())
      .then(data => setManga(data))
      .catch(error => console.error(error));
  }, []);

  if (!manga) {
    return <div>Loading...</div>;
  }

  return (
    <div className="fiche-manga">
      <h2>{manga.titreFr}</h2>
      <img src={manga.image} alt={manga.titreFr} />
      <p>{manga.resume}</p>
      {/* Affichez les autres donn√©es du manga ici */}
    </div>
  );
}

export default FicheManga;
