import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './styles/connection.css';

const Connection = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = async (e) => {
    e.preventDefault();

    // Vérification du pseudo et du mot de passe via l'API
    try {
      const response = await fetch(
        `http://localhost:8080/Backend/API?action=Connexion&pseudo=${username}&password=${password}`
      );

      if (response.ok) {
        // Succès de la connexion
        console.log('Connexion réussie');
      } else {
        // Échec de la connexion
        console.log('Échec de la connexion');
      }
    } catch (error) {
      console.log('Erreur lors de la connexion:', error);
    }
  };

  const handleSignup = () => {
    // Redirection vers le module d'inscription
    window.location.href = '/inscription';
  };

  return (
    <div className="container">
      <h2>Connexion</h2>
      <form onSubmit={handleLogin}>
        <input
          type="text"
          placeholder="Nom d'utilisateur"
          required
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />
        <input
          type="password"
          placeholder="Mot de passe"
          required
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />
        <input type="submit" value="Se connecter" />
        <Link to="/inscription">      
            <input type="button" value="Pas encore inscrit ? C'est par ici" />
        </Link>
      </form>
    </div>
  );
};

export default Connection;
